<template>
  <div :style="styles" :class="['nut-avatar', 'avatar-'+size,'avatar-'+shape]" @click="activeAvatar">
    <i class="icon" :style="iconStyles"></i>
    <span class="text"><slot></slot></span>
  </div>
</template>
<script>
export default {
  name: 'nut-avatar',
  props: {
    size: {
      type: String,
      default: 'normal'
    },
    shape: {
      type: String,
      default: 'round'
    },
    bgColor: {
      type: String,
      default: '#eee'
    },
    bgIcon: {
      type: String,
      default: 'data:image/png;base64,'
    },
    bgImage: {
      type: String,
      default: ''
    }
  },
  computed: {
    styles () {
      return {
        'background': this.bgColor + ' url(' + this.bgImage + ')' + 'no-repeat',
        'backgroundSize': '100%'
      }
    },
    iconStyles () {
      return {
        'backgroundImage': 'url(' + this.bgIcon + ')'
      }
    }
  },
  data () {
    return {}
  },
  methods: {
    activeAvatar () {
      this.$emit('activeAvatar')
    }
  }
}
</script>
<style lang="scss">
  @import './avatar.scss';
</style>
